package problems;

public class chapter2_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0; i<5; i++)
			 if(i==2) continue;
			 else System.out.println(i);
		
		int i=0; 
		while(i<5){
			 if(i==2) continue;
			 else System.out.println(i++);
		}

	}

}
